int yueshu(int a,int b)
{
	int t;
	while(b)
	{
		t=a%b;
		a=b;
		b=t;
	}
	return a;
}
int beishu(int a,int b)
{
	int t=yueshu(a,b);
	return a*b/t;
}
int main()
{
	int i,j,a,b;
	scanf("%d%d",&a,&b);
	printf("%d %d",yueshu(a,b),beishu(a,b));
}

