using namespace std;
int gcd(int m,int n)
{
	int r=m%n;
	while (r)
	{
		m=n;
		n=r;
		r=m%n;
	}
	return n;
}
int lcd(int m,int n)
{
	return m*n/gcd(m,n);
}
int main()
{
	int m,n;
	cin>>m>>n;
	cout<<gcd(m,n)<<" "<<lcd(m,n)<<endl;
	return 0;
}

