#define ElemType int
typedef struct LNode{
	ElemType data;
	struct LNode *next;
}LNode,*LinkList;
void initial(LinkList L){
	char temp;
	ElemType data;
	LNode *p = L;
	while(scanf("%d",&data)!=NULL){
		LNode *s = (LNode *)malloc(sizeof(LNode));
		s->data = data;
		p->next = s;
		p = p->next;
		temp=getchar();
		if(temp=='\n'||temp==NULL)
			break;
	}
	p->next = NULL;
}

int find(LinkList L,ElemType data){
	int no = 0;
	LNode *p = L->next;
	while(p){
		no++;
		if(p->data==data)
			break;
		else
			p=p->next;
	}
	return no;
}

int main(){
	ElemType data;
	LinkList L = (LNode *)malloc(sizeof(LNode));
	initial(L);
	scanf("%d",&data);
	printf("%d",find(L,data));
	return 0;
}

