		else return;

		t = 38-10-a[1];
		if (valid(t)) a[10] = t;
		else return;

		t = 38-a[6]-a[2];
		if (valid(t)) a[11] = t;
		else return;

		t = 38-a[2]-a[3];
		if (valid(t)) a[15] = t;
		else return;

		t = 38-a[3]-a[10];
		if (valid(t)) a[14] = t;
		else return;

		t = 38-13-a[4]-a[11];
		if (valid(t)) a[7] = t;
		else return;

		t = 38-a[0]-a[1]-a[4];
		if (valid(t)) a[5] = t;
		else return;

		t = 38-a[0]-a[7]-a[15];
		if (valid(t)) a[12] = t;
		else return;

		t = 38-13-a[5]-a[14];
		if (valid(t)) a[9] = t;
		else return;

		t = 38-a[11]-a[12]-a[14];
		if (valid(t)) a[13] = t;
		else return;

		t = 38-a[6]-a[7]-a[9]-a[10];
		if (valid(t)) a[8] = t;
		else return;

		//if(15+a[4]+a[8]+a[13]+a[3]!=38) return;//这三行可不判断 
