{
	int i,j,sum=0;
	for(j=0;j&lt;N;j++)//行
	for(i=0;i&lt;N;i++)//列&nbsp;
		sum+=a[i][j];
	printf(&quot;%d\n&quot;,sum);
	return 0;
}

