	else if(m!=0&&n==0)return ack(m-1,1);
	else return ack(m-1,ack(m,n-1));
}
int main(){
	cin>>m>>n;
	cout<<ack(m,n);
    return 0;
}

def ack(m,n):
    if(m==0):
        y=n+1
    elif(n==0):
        y=ack(m-1,1)
    else:
        y=ack(m-1,ack(m,n-1))
    return(y)
