#include<stdio.h>
#include<math.h>

int main()
{
	int i,j=0;
	double a,s=0;
	for(i=1;i<1000000;i=i+2)
	{
		a=1/(double)i*pow(-1,j);
		if(fabs(a)<0.000001)
			break;
		else
			s+=a;
		j++;
	}
	printf("%.6lf\n",4*s);
	return 0;
}

#include<stdio.h>
#include<math.h>
int main()
{
  double sum=0,a=1;int k=1;
  while(a>=1e-6)
  {

    if(k%2==0)
    sum=sum-a;
    else sum=sum+a;
    a=1.0/(1+2*k);
    k++;
  }
  printf("%.6lf\n",sum*4);
  return 0;
}

