#include<iostream>
#include<cstdio>
using namespace std;
int main()
{
	int a,b,c;
	cin>>a>>b>>c;
	double sum=0;
	for (int i=1; i<=a; i++) sum+=i;
	for (int i=1; i<=b; i++) sum+=i*i;
	for (int i=1; i<=c; i++) sum+=1.0/i;
	printf("%.2lf\n",sum);
	return 0;
}

#include<iostream>
using namespace std;
int main()
{
	int g,s,b;
	for (int i=100; i<999; i++)
	{
		b=i/100;
		s=i/10%10;
		g=i%10;
		if (b*b*b+s*s*s+g*g*g==i) cout<<i<<endl;
	}
	return 0;
}

#include<iostream>
using namespace std;
int main()
{
	int n;
	cin>>n;
	for (int i=6; i<=n; i++)
	{
		int sum=0;
		for (int j=1; j<=i/2; j++)
			if (i%j==0) sum+=j;
		if (i==sum)
		{
			cout<<i<<" its factors are ";
			for (int j=1; j<=i/2; j++)
				if (i%j==0) cout<<j<<" ";
			cout<<endl;
		}
	}
	return 0;
}

#include<iostream>
#include<cstdio>
using namespace std;
int main()
{
	int n;
	cin>>n;
	double fz=2,fm=1,sum=0;
	for (int i=1; i<=n; i++)
	{
		sum+=fz/fm;
		double t=fz;
		fz+=fm;
		fm=t;
	}
	printf("%.2lf\n",sum);
	return 0;
}

#include<stdio.h>
#include<math.h>
double total(double m,int n)
{
	if(n==1)  return m;
	else return total(m,n-1)+m/pow(2,n-2);
} 
int main()
{
	int i,j;
	int n;
	double m;
	scanf("%lf%d",&m,&n);
	double gaodu=m/pow(2,n);
	double zong=total(m,n);
	printf("%.2lf %.2lf",gaodu,zong);
 }

#include<iostream>
#include<cstdio>
using namespace std;
int main()
{
	double n,sum;
	int m;
	cin>>n>>m;
	sum=-n;
	for (int i=1; i<=m; i++)
	{
		sum+=2*n;
		n/=2;
	}
	printf("%.2lf %.2lf\n",n,sum);
	return 0;
}

#include<stdio.h>
