#include<stdio.h>
#include<math.h>
int yueshu(int a,int b)
{
	int t;
	while(b)
	{
		t=a%b;
		a=b;
		b=t;
	}
	return a;
}
int beishu(int a,int b)
{
	int t=yueshu(a,b);
	return a*b/t;
}
int main()
{
	int i,j,a,b;
	scanf("%d%d",&a,&b);
	printf("%d %d",yueshu(a,b),beishu(a,b));
}

#include<iostream>
using namespace std;
int gcd(int m,int n)
{
	int r=m%n;
	while (r)
	{
		m=n;
		n=r;
		r=m%n;
	}
	return n;
}
int lcd(int m,int n)
{
	return m*n/gcd(m,n);
}
int main()
{
	int m,n;
	cin>>m>>n;
	cout<<gcd(m,n)<<" "<<lcd(m,n)<<endl;
	return 0;
}

#include<stdio.h>
#include<math.h>
double dayu(double a,double b,double c)
{
	double m1=(-b+sqrt(b*b-4*a*c))/(a*2);
	double m2=(-b-sqrt(b*b-4*a*c))/(a*2);
	printf("x1=%.3lf x2=%.3lf",m1,m2);
}
double dengyu(double a,double b,double c)
{
	double m1=(-b)/(2*a);
	printf("x1=x2=%.3lf",m1);
}
double xiaoyu(double a,double b,double c)
{
	double m1=(-b)/(2*a);
	double t=sqrt(4*a*c-b*b)/(2*a);
	printf("x1=%.3lf+%.3lfi x2=%.3lf-%.3lfi",m1,t,m1,t);
}
int main()
{
    int i,j;
	double a,b,c;
	scanf("%lf%lf%lf",&a,&b,&c);
	if(pow(b,2)-4*a*c>0)  dayu(a,b,c);
	else if(pow(b,2)-4*a*c==0)  dengyu(a,b,c);
	else xiaoyu(a,b,c);
}

#include<iostream>
#include<cmath>
#include<cstdio>
using namespace std;
double disc,x1,x2,p,q;
void big(int a,int b)	//>0
{
	x1=(-b+sqrt(disc))/(2.0*a);
	x2=(-b-sqrt(disc))/(2.0*a);
}
void small(int a,int b)	//<0
{
	p=-1.0*b/(2*a);
	q=sqrt(-disc)/(2*a);
}
void equal(int a,int b)	//==0
{
	x1=x2=-1.0*b/(2*a);
}
int main()
{
	int a,b,c;
	cin>>a>>b>>c;
	disc=b*b-4*a*c;
	if (disc>0)
	{
		big(a,b);
		printf("x1=%.3lf x2=%.3lf\n",x1,x2);
	}
	else if (disc==0)
	{
		equal(a,b);
		printf("x1=x2=%.3lf\n",x1);
	}
	else
	{
		small(a,b);
		printf("x1=%.3lf+%.3lfi x2=%.3lf-%.3lfi\n",p,q,p,q);
	}
	return 0;
}

#include<stdio.h>
#include<math.h>
void panduan(int n)
{
	int i;
	int a[10000]={1,1};
	for(i=2;i<=sqrt(n);i++)
	{
		if(n%i==0)  a[n]=1;break;
	}
	if(a[n]==0)  printf("prime");
	else printf("not prime"); 
}
int main()
{
    int n;
    scanf("%d",&n);
    panduan(n);
}

#include<iostream>
#include<cmath>
using namespace std;
bool prime(int n)
{
	int i,t=sqrt(n);
	for (i=2; i<=t; i++)
		if (n%i==0) break;
	return i>t;
}
int main()
{
	int n;
	cin>>n;
	if (prime(n)) cout<<"prime"<<endl;
	else cout<<"not prime"<<endl;
	return 0;
}

#include<stdio.h>
int convert(int m[3][3])
{
    int b[3][3],i,j;
    for( i=0;i<3;i++)
    {
        for( j=0;j<3;j++)
        {
            b[i][j]=m[j][i];

        }
    }
    for( i=0;i<3;i++)
    {
        for( j=0;j<3;j++)
        {
            printf("%d\t",b[i][j]);
        }
        printf("\n");
    }



}


