import java.io.UnsupportedEncodingException;
import java.util.Scanner;
public class Main 
{
	public static void main(String[] args) throws UnsupportedEncodingException 
    {	
	int p[] = {0xB0C5,0xB2C1,0xB4EE,0xB6EA,0xB7A2,0xB8C1,0xB9FE,0xBBF7,0xBFA6,0xC0AC,0xC2E8,0xC4C3,0xC5B6,0xC5BE,0xC6DA,0xC8BB,0xC8F6,0xCBFA,0xCDDA,0xCEF4,0xD1B9,0xD4D1,0xFFFF};
    	String h="ABCDEFGHJKLMNOPQRSTWXYZ";
    	int i,j,k;
    	Scanner sc=new Scanner(System.in);
    	int n=Integer.parseInt(sc.nextLine());
    	for(k=0;k<n;k++)
    	{
    		String r="";
    		String s=sc.nextLine();
    		byte[] b=s.getBytes("GB2312"); 
    		for(i=0;i<b.length;i+=2)//针对每个汉字 
    		{
    			int t=(0xff00&b[i]<<8)+(0xff&b[i+1]);//位运算，把每个汉字转成整数
    			for(j=0;j<23;j++)
    			{
    				if(t<p[j])
    				{
    					r+=h.charAt(j);
    					break;
    				}
    			}
    		}
    		System.out.println(r);
    	}
    }
}

#include <stdio.h>
#include <string.h>
int main()
{
	int i,l;
	char a[100];
	gets(a);
	l=strlen(a);

	for(i=0;i<l;i++)
	{

		    if(a[i]=='a')
		    a[i]='v';
		    else if(a[i]=='b')
		    a[i]='w';
			else if(a[i]=='c')
		    a[i]='x';
		    else if(a[i]=='d')
		    a[i]='y';
		    else if(a[i]=='e')
		    a[i]='z';




		     else if(a[i]=='A')
		    a[i]='V';
		    else if(a[i]=='B')
		    a[i]='W';
		    else if(a[i]=='C')
		    a[i]='X';
		    else if(a[i]=='D')
		    a[i]='Y';
		    else if(a[i]=='E')
		    a[i]='Z';


		else if(a[i]==' ')
		{
			a[i]=' ';
		}
		else if(a[i]>='f'&&a[i]<='z'||a[i]>='F'&&a[i]<='Z')
		{
			a[i]-=5;
		}
	}
	puts(a);
	return 0;
}

#include<iostream>
using namespace std;
int main()
{
	string a = "vwxyzabcdefghijklmnopqrstu";//密文转原文的表,小写
	string b = "VWXYZABCDEFGHIJKLMNOPQRSTU";//密文转原文的表,大写 
	char c;
	while(cin.get(c))
	{
		if(c>='a'&&c<='z')
		    cout<<a[c-'a'];
		else if(c>='A'&&c<='Z')
		    cout<<b[c-'A'];
		else
			cout<<c;
	}
	return 0;
}

#include <stdio.h>
#include <string.h>
int main()
{
	int n,value,i,sum;
	char a[1001],temp;
	int tab[26]; 
	scanf("%d",&n);
	tab['I'] = 1;
    tab['V'] = 5;
    tab['X'] = 10;
    tab['L'] = 50;
    tab['C'] = 100;
    tab['D'] = 500;
    tab['M'] = 1000;
	while(n--)
	{
		scanf("%s",a);
		sum=0;
		for(i=0;i<strlen(a);i++)
		{
			value=tab[a[i]];
			temp=a[i+1];
			if((temp=='\n')||(temp=='\0')||(tab[temp]<=value))
			{
				sum+=value;
			}
			else
			{
				sum-=value;
			} 
		}
		printf("%d\n",sum); 
	}
	return 0;
 }

#include <string>
#include <iostream>
using namespace std;
int main()
{
	static int arab[] = {1,5,10,50,100,500,1000};
	static char rome[] = {'I','V','X','L','C','D','M'};
	string s[100];//n<100
	int dec[100],n,i,j;
	cin>>n;
	for(int k=0;k<n;k++)
	{
		cin>>s[k];
		int sum=0,former=0;//总和、当前数左边的数
		for(i=0;i<s[k].length();i++)
		{
			for(j=0;j<7;j++)
			{
				if(s[k][i]==rome[j])
				{
					if(arab[j]>former&&former)//当前数如果大于左边的
						former=-former;
					sum+=former;
					former=arab[j];
					if(i==s[k].length()-1)
						sum+=former;
				}
			}
		}
		dec[k]=sum;
	}
	for(i=0;i<n;i++) cout<<dec[i]<<endl;
	return 0;
}

import java.util.Scanner;

public class Main{

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		Scanner in = new Scanner(System.in);
		Scanner in1 = new Scanner(System.in);
		int n = in.nextInt();
		for (int i = 0; i < n; i++) {
			String s = in.next();
			int[] a = new int[s.length() + 1];
			for (int j = 0; j < s.length(); j++) {
				a[j] = lm(s.charAt(j));

			}
			//System.out.println(a[2]);
			int sum = 0;
			for (int x = s.length() - 1; x >= 0; x--) {
				if (sum > a[x] && a[x] != a[x + 1]) {

					sum = sum - a[x];
				} else {
					// System.out.println("111");
					sum += a[x];
					// System.out.println(x);
				}
				// System.out.println(sum);
				// System.out.println(a[x]);
			}
			// if(sum>a[0])
			// System.out.println(sum-a[0]);
			// else
			System.out.println(sum);
		}

	}

	static int lm(char c) {
		switch (c) {
		case 'I':
			return 1;
		case 'V':
			return 5;
		case 'X':
			return 10;
		case 'C':
			return 100;

		case 'L':
			return 50;
		case 'D':
			return 500;
		case 'M':
			return 1000;
		default:
			break;
		}

		return 1;

	}
}

#include <stdio.h>
#define  N 29
#define  M 4
void perms(int p[],int start)
{
	int i,t;
	if(start==M)
	{
		if((p[0]<p[1])&&(p[0]<p[2])&&(p[2]<p[3])&&(p[0]*p[0]*p[0]+p[1]*p[1]*p[1]==p[2]*p[2]*p[2]+p[3]*p[3]*p[3]))
				printf("%d,%d,%d,%d\n",p[0],p[2],p[3],p[1]);
		return;
	}
	for(i=start;i<N;i++)// 注意i从start开始，不从0开始哦
	{
		t=p[i];p[i]=p[start];p[start]=t;//交换
		perms(p,start+1);//递归
		t=p[i];p[i]=p[start];p[start]=t;//交换回来
	}
}
int main()
{
	int i,p[N];
	for(i=0;i<N;i++) p[i]=i+1;//数组赋初值		
	perms(p,0);//从数组中索引号为0的元素开始进行排列
	return 0;
}

#include<iostream>
#include<cmath>
using namespace std;
#define N 4
void F(int a[],int start)
{
	int i,j,t;
	if(start==N)
	{
		int x=pow(a[0],3);
		int y=pow(a[1],3);
		int z=pow(a[2],3);
		int w=pow(a[3],3);
		if(a[2]>a[1]&&a[3]>a[2]&&a[1]>a[0]&&((x+y)==(w+z)||(x+z)==(w+y)||(x+w)==(z+y)) ){cout<<a[0]<<','<<a[1]<<','<<a[2]<<','<<a[3]<<endl;}
		return;
	}
	for(i=start;i<30;i++)
	{

		swap(a[start],a[i]);
		F(a,start+1);
		swap(a[start],a[i]);


	}
}
int main()
{
	int p[30];
	int i;
	for(i=0;i<29;i++)
    	p[i]=i+1;
	F(p,0);

	return 0;
}

#include <stdio.h>
#define  N 10
#define  M 6
void perms(int p[],int start)
{
	int i,t;
	if(start==M)
	{
		if((p[0]*100+p[1]*10+p[2])*(p[0]*100+p[1]*10+p[2])==(p[0]*100000+p[3]*10000+p[4]*1000+p[5]*100+p[3]*10+p[1]))
				printf("%d%d%d\n",p[0],p[1],p[2]);
		return;
	}
	for(i=start;i<N;i++)// 注意i从start开始，不从0开始哦
	{
		t=p[i];p[i]=p[start];p[start]=t;//交换
		perms(p,start+1);//递归
		t=p[i];p[i]=p[start];p[start]=t;//交换回来
	}
}
int main()
{
	int p[N];
	for(int i=0;i<N;i++) p[i]=i;//数组赋初值		
	perms(p,0);//从数组中索引号为0的元素开始进行排列
	return 0;
}

