import java.util.Scanner;


public class Main {


	public static int f(int n)
	{
		if(n==1)
		{
			return 2;
		}else
		{
			return n+f(n-1);
		}
	}

	public static void main(String[] args) {

		Scanner scan=new Scanner(System.in);
		while(scan.hasNext())
		{
			int n=scan.nextInt();
			int sum=0;
			if(n!=0)
			{
				sum=f(n);
				System.out.println(sum);
			}

		}


	}

}

