var
  s1,s:string;
  k,t,x:array[1..10000] of integer;
  f,n,r,h,i:longint;
begin

  readln(r,n);
  for i:=1 to n do
    begin
      readln(s);
      s1:=copy(s,1,pos(' ',s)-1);
      val(s1,h);
      delete(s,1,length(s1)+1);
      s1:=copy(s,1,pos(' ',s)-1);
      if s1='START' then
        begin
          delete(s,1,length(s1)+1);
          s1:=copy(s,1,pos(' ',s)-1);
          val(s1,k[h]);
          k[h]:=k[h]*60;
          delete(s,1,length(s1)+1);
          s1:=copy(s,1,length(s));
          val(s1,f);
          k[h]:=k[h]+f;
        end;
      if s1='STOP' then
        begin
          delete(s,1,length(s1)+1);
          s1:=copy(s,1,pos(' ',s)-1);
          val(s1,t[h]);
          t[h]:=t[h]*60;
          delete(s,1,length(s1)+1);
          s1:=copy(s,1,length(s));
          val(s1,f);
          t[h]:=t[h]+f;
        end;
      if (k[h]<>0) and (t[h]<>0) then
        begin
          x[h]:=x[h]+(t[h]-k[h]);
          k[h]:=0;
          t[h]:=0;
        end;
    end;
  for i:=1 to r do
    writeln(x[i] div 60,' ',x[i] mod 60);
end.

