#include <stdio.h>
int main()
{
int a[100][100],i,j=1,k=0,n,m;
scanf("%d",&n);
m=n;
while(1)
{
for(i=k;i<n;i++)
a[i][n-1]=j++;
if(j==m*m+1) break;
for(i=n-2;i>=k;i--)
a[n-1][i]=j++;
for(i=n-2;i>=k;i--)
a[i][k]=j++;
for(i=k+1;i<n-1;i++)
a[k][i]=j++;
n--;
k++;
}
for(i=0;i<m;i++)
{
printf("%d",a[i][0]);
for(j=1;j<m;j++)
printf(" %d",a[i][j]);
printf("\n");
}
}

