#include <stdio.h>
int main()
{
    int a,b,c,d;
    while(scanf("%d%d%d",&a,&b,&c)!=EOF)
    {
        if(a%3==0)a=0;
        if(b%5==0)b=0;
        if(c%7==0)c=0;
        for(d=10;d<=100;d++)
        {
            if(d%3==a&&d%5==b&&d%7==c){printf("%d\n",d);break;}
        }
        if(d%3!=a||d%5!=b||d%7!=c){printf("No answer\n");}
    }
    return 0;
}

