#include<stdio.h>
int main()
{
    int n,t,p,q[100000],s;
    char a[50];
    while(scanf("%d%d",&n,&t)!=EOF)
    {
        getchar();
        for(p=0;p<n;p++)
        {
            q[p]=0;
        }
        while(t--)
        {
            scanf("%s",&a);
            if(a[0]=='C')
            {
                scanf("%d",&p);
                if(q[p-1])q[p-1]=0;
                else q[p-1]=1;
            }
            else
            {
                s=0;
                for(p=0;p<n;p++)
                {
                    s+=q[p];
                }
                printf("%d\n",s);
            }
        }
    }
    return 0;
}


#include<stdio.h>
bool a[100010];
int main()
{
	int n,m,t,k,l,count=0;
	char c[10];
	scanf("%d%d",&n,&t);
	while(t--)
	{
		scanf("%s",c);
		if(c[0]=='C')
		{
			scanf("%d",&l);
			a[l]=!a[l];
			if(a[l]==1)
			count++;
			else
				count--;
		}
		if(c[0]=='Q')
			printf("%d\n",count);
	}
return 0;
}

#include<stdio.h>
int main()
{
    int q,tian[1000],king[1000],p,t,win,m,k,n;
    while(scanf("%d",&n)!=EOF)
    {
        for(p=0;p<n;p++)scanf("%d",&tian[p]);
        for(p=0;p<n;p++)scanf("%d",&king[p]);
        for(p=0;p<n;p++)
        {
            for(t=1;t<n-p;t++)
            {
                if(king[t]>king[t-1]){q=king[t];king[t]=king[t-1];king[t-1]=q;}
                if(tian[t]>tian[t-1]){q=tian[t];tian[t]=tian[t-1];tian[t-1]=q;}
            }
        }
        win=0;
        for(p=n-1;p>=0;p--)
        {
            q=1;
            for(t=n-1;t>=0;t--)
            {
                if(king[p]<tian[t]){win++;q=0;tian[t]=-1;break;}
                if(king[p]==tian[t]){
                        if(tian[t-1]>king[p])continue;
                        else {q=0;tian[t]=-1;break;}}
            }
            if(q==1)win=win-1;
        }
        printf("%d\n",win*200);
    }
    return 0;
}


#include<stdio.h>
#include<algorithm>
using namespace std;
int main()
{
    int a[1005],b[1005],i,a_first,a_last,b_first,b_last,n,A,B;
    while(scanf("%d",&n)!=EOF)
    {
        A=0;B=0;
        a_first=1; a_last=n;
        b_first=1; b_last=n;
        for(i=1;i<=n;i++)
           scanf("%d",&a[i]);
        for(i=1;i<=n;i++)
           scanf("%d",&b[i]);
        sort(a+1,a+n+1);
        sort(b+1,b+n+1);
        for(i=1;i<=n;i++)
        {
            if(a[a_first]>b[b_first])
            {
                a_first++;b_first++;A++;
            }
            else if(a[a_first]<b[b_first])
            {
                  b_last--;a_first++;B++;
            }
            else if(a[a_last]>b[b_last])
            {
                a_last--;b_last--;A++;
            }
            else if(a[a_last]<b[b_last])
            {
                 b_last--;a_first++;B++;
            }
            else if(a[a_first]<b[b_last])
            {
                 b_last--;a_first++;B++;
            }
        }
        printf("%d\n",(A-B)*200);
    }
}

