#include<cstdio>
#include<cstring>
#include<cmath>
#include<iostream>
#include<algorithm>
using namespace std;
char s[1005];
char ans[20][10]={"ling","yi","er","san","si","wu","liu","qi","ba","jiu"};
int res[15];
int main()
{
    //freopen("input.txt","r",stdin);
    //freopen("output.txt","w",stdout);
    int t;
    scanf("%d",&t);
    while(t--)
    {
        scanf("%s",s);
        int sum=0;
        int len=strlen(s);
        for(int i=0;i<len;i++)
        {
            sum=sum+s[i]-'0';
        }
       int cnt=0;
       while(sum)
       {
           res[cnt++]=sum%10;
           sum/=10;
       }
       for(int i=cnt-1;i>=1;i--)
       {
           printf("%s ",ans[res[i]]);
       }
       printf("%s\n",ans[res[0]]);
    }
    return 0;
}

