#include <iostream>
using namespace std;
#define N 6
int a[N];
void f(int k)
{
	int i;
	if(k==N)
	{
		int sum=0;
		for(i=0;i<N;i++)
			sum+=a[i];
		bool b1,b2,b3,b4;
		b1=a[1]+a[2]==2||a[0]==0;
		b2=a[2]+a[3]==0||a[4]==0;
		b3=a[5]==0||a[4]==1;
		b4=a[0]==1;
		if(sum==4&&b1&&b2&&b3&&b4)
		{
			for(i=0;i<N;i++)
				if(a[i]==0)
					cout<<char('A'+i);
			cout<<endl;
		}
		return;
	}
	for(i=0;i<2;i++)
	{
		a[k]=i;
		f(k+1);
	}
}
int main()
{	
	f(0);
	return 0;
}

#include<iostream>
using namespace std;
#define N 8//8个可能有正负号的空位 
int a[N];
void f(int idx)
{
	int i;
	if(idx==N)
	{
		int sum=0;
		int t=1;//赋值为第一个数1
		int former=1;//第一个数前的符号肯定是+号
		for(i=0;i<N;i++)
		{
			if(a[i]==0)//无符号
				t=t*10+i+2;
			else//+或-号
			{
				sum+=t*former;
				former=a[i];
				t=i+2;
			}
		}
		sum+=t*former;
		if(sum==110)
		{
			for(i=0;i<N;i++)
			{
				cout<<i+1;					
				if(a[i]==1)
					cout<<'+';
				else if(a[i]==-1)
					cout<<'-';
			}
			cout<<9<<endl;
		}
		return;
	}
	for(i=-1;i<2;i++)
	{
		a[idx]=i;
		f(idx+1);
	} 
}
int main()
{
	f(0);
	return 0;
}

#include<iostream>
using namespace std;
int cnt=0;
void f(int k,int score)
{
	int i;
	if(k>10||score>90||score+(10-k)*10<90)//剪枝
	{
		return;
	}
	if(score==90)
	{
		cnt++;
		return;
	}

	for(i=0;i<=10;i++)
	{
		f(k+1,score+i);
	}
}
int main()
{
	f(0,0);
	cout<<cnt;
	return 0;
}

#include <iostream>
using namespace std;
int cnt=0;
void f(int sum,int n)
{
	int i;
	if(sum>13||(13-n)*4+sum<13||n>13) return;//剪枝 
	if(sum==13) 
	{
		cnt++;
		return;	
	}
	for(i=0;i<=4;i++)
	{
		f(sum+i,n+1);
	}
}
int main()
{
	f(0,0);
	cout<<cnt;
	return 0;
}

