#include<iostream>
using namespace std;

int main(){
	int ans=0;
	for (int i=1; i<=1000; i++){
		int cnt0=0,cnt1=0,x=i;
		while (x){
			if (x%2) cnt1++;
			else cnt0++;
			x/=2;
		}
		if (cnt1>cnt0) ans++;
	}
	cout<<ans<<" "<<1000-ans<<endl;
	return 0;
}

#include <stdio.h>
int main()
{
	int M,n;
	scanf("%d",&M);
	scanf("%d",&n);
	int w[n],v[n],wv[n];

	for(int i=0;i<n;i++)
	{
		scanf("%d",&w[i]);
		scanf("%d",&v[i]);
		wv[i]=w[i]*v[i];
	}
