#define N 4
int a[N]; 
void f(int k)
{
	int i; 
	if(k==N)
	{
		int sum=0;
		for(i=0;i<N;i++)
			sum+=a[i];
		if(sum==1)//只有一个1 
		{
			if(!a[1]==a[3]&&!a[1]==a[2]&&!a[0]==a[1])
			{
				for (i = 0; i < N; i++)
				{
					if(a[i]) cout<<char('A'+i);
				}
			}
		}
		return;
	}
	for(i=0;i<2;i++)
	{
		a[k]=i;
		f(k+1);
	}
}
