#include<iostream>
#include<algorithm>
using namespace std;
#define N 5 
int main()
{
	int a[N]={1,0,0,0,0};//1扶了。0没扶。
	do
	{
		//ABCDE5个变量存5个人的真假话状态，1为真话,0为假话 
		int A=(a[0]+a[4]==0);//A ：我和E都没有扶老奶奶
		int B=(a[2]+a[4]==1);//B ：老奶奶是被C和E其中一个扶过大街的
		int C=(a[2]+a[3]==1);//C ：老奶奶是被我和D其中一个扶过大街的
		int D=(a[1]+a[2]==0);//D ：B和C都没有扶老奶奶过街
		int E=(a[4]==0);//E ：我没有扶老奶奶
		if(A+B+C+D+E==2)//5个人有且只有２个人说的是真话
		{
			for (int i = 0; i < N; i++)
			{
				if(a[i]) cout<<char('A'+i)<<' ';
			}
		}
	}
	while(prev_permutation(a,a+N));
	return 0;
}

import java.util.Arrays;
public class Main{
     public static void main(String[] args) {
         boolean[] arr = new boolean[5];
         int sum;
         for(int i = 0;i < 5;i++) {
             Arrays.fill(arr, false);
             arr[i] = true;
             sum = 0;
             if (!arr[0] && !arr[4])
                 sum++;
             if (arr[2] || arr[4])
                 sum++;
             if (arr[2] || arr[3])
                 sum++;
             if (!arr[1] && !arr[2])
                 sum++;
             if (!arr[4])
                 sum++;
             if (sum == 2) {
                 System.out.print((char) ('A' + i) + " ");
             }
         }
     }
 }

#include<iostream>
using namespace std;
#define N 6
int a[N];//存放ABCDEF是否是罪犯的状态，1是罪犯，0不是罪犯 
int v[N];//判断出是罪犯的记录 
void f(int idx)
{
	if(idx==N)
	{
		int sum=0,i;
		for(i=0;i<N;i++)
			sum+=a[i];
		if(sum==1)//凶手只有一个
		{
			//ABCDEF6个变量存6个人的真假话状态，1为真话,0为假话 
			int A=(a[0]==0);//A：我不是罪犯
			int B=(a[0]+a[2]==1);//B：A、C中有一个是罪犯
			int C=(A+B==0);//C：A和B说了假话
			int F=(a[5]==1);//F：我是罪犯
			int D=(C+F==0);//D：C和F说了假话
			int E=(A+D==2&&B+C+F==0);//E：其他五个人中，只有A和D说了真话
			if(A+B+C+D+E+F==3)//他们中只有一半说了真话
			{
				for(i=0;i<N;i++)
				{
					if(a[i])
						v[i]=1; 
				}
			}

		}
		return;
	}
	a[idx]=0;
	f(idx+1);
	a[idx]=1;
	f(idx+1);
}
int main()
{
	f(0);
	for(int i=0;i<N;i++)
	{
		if(v[i])
			cout<<char(i+'A')<<endl;
	}
	return 0;
}

#define N 1100000
#include <map>
#include <string>
#include <iostream>
using namespace std;

int p[N];
map<string,int> nn;
int numseen;
int cnt[N];

int lookup(int i) {
    if(i!=p[i]) p[i] = lookup(p[i]);
    return p[i];
}
void merge(int i, int j) {
    int ii = lookup(i);
    int jj = lookup(j);
    if(ii==jj) return;
    if(ii<jj) {
        p[jj] = ii;
        cnt[ii] += cnt[jj];
    } else {
        p[ii] = jj;
        cnt[jj] += cnt[ii];
    }
}
int find(string s) {
    int ret = nn[s];
    if(ret == 0) {
        numseen++;
        ret = numseen;
        nn[s] = ret;
    }
    return ret;
}
main() {
    int CASES;
    cin >> CASES;
    while(CASES--) {
        int i,j;
        for(i=0;i<N;i++) p[i] = i;
        for(i=0;i<N;i++) cnt[i] = 1;
        nn = map<string,int>();
        numseen = 0;
        int num;
        cin >> num;
        for(j=0;j<num;j++) {
            string nm1, nm2;
            cin >> nm1;
            cin >> nm2;
            int n1 = find(nm1);
            int n2 = find(nm2);
            merge(n1, n2);
            cout << cnt[lookup(n1)] << endl;
        }
    }
}

