#include<stdio.h>

int prime(int n)
{
	int i;
	for(i=2;i*i<=n;i++)
		if(n%i==0)
			return 0;
		return 1;
}
int main()
{
    int a,b,c,i=0;
    for(a=1;a<=9;a++)
		for(b=2;b<=9;b++)
			for(c=0;c<=7;c++)
				if(a!=b&&a!=c&&b!=c&&b>(a+c)&&!prime(a+b))
				{
					i++;
					if(i==70)
					break;
					else
					{
					if(i%8==0)
						printf("%d%d%d \n",a,b,c);
					else
						printf("%d%d%d ",a,b,c);
					}
				}
					printf("791 \n");
}

