#include<stdio.h>
int ishw(int n){
	int m=0,t,k=n;
	while(k!=0){
		t=k%10;
		m=m*10+t;
		k=k/10;
	}
	if(m==n){
		return 1;

	}else{
		return 0;
	}
}
int iszs(int n){
	int i;
	if(n<2){
		return 0;
	} 
	for(i=2;i<=n-1;i++){
		if(n%i==0){
			return 0;
		}
	}
	return 1;
}
int main()
{
	int i;
	for(i=10;i<=1000;i++){
		if(ishw(i)==1&&iszs(i)==1){
			printf("%d\n",i);
		}
	}




return 0;
}

#include <cstdio>
int main(void) {
	const int MAXN=1001;
	int i, j, d, h[MAXN]={1,1,0}; //1：表示下标为非质数，0表示下标为质数
	for(i=2; i<MAXN; ++i)
		for(j=2; i*j<MAXN; ++j) h[i*j]=1;
	for(i=10; i<MAXN; ++i) {
		for(j=0, d=i; d>0; d/=10) j = 10*j+d%10;
		if(i==j && h[i]==0) printf("%d\n", i);
	}
	return 0;
}

import math
def suShu(n):
        f = True
        for i in range (2 , int(math.sqrt(n))+1):
                if n % i == 0:
                        f = False
                        break;
        return f and n != 1
def hwsuShu(n):
        a = n // 100 % 10
        b = n // 10 % 10
        c = n % 10
        if n < 100:
             return  b == c and  suShu(n);
        elif n > 100:
             return  a == c and  suShu(n);
        else:
             return False
for i in range (10, 1000):
        if hwsuShu(i):
                print(i)

#include<stdio.h>
int sushu(int n);
main()
{
	int i,a,b;
	for(i=10;i<100;i++)
	{
		a=i%10;
		b=a*10+i/10;
		if(sushu(b)==1&&sushu(i)==1)
		{
			printf("%d\n",i);
		}
	}
}
int sushu(int n)
{
	int i;
	for(i=2;i<n;i++)
	{
		if(n%i==0)
		{
			return 0;
		}
	}
	if(n==1)
	{
		return 0;
	}
	else
	    return 1;
}

#include <bits/stdc++.h>
using namespace std;

bool sushu(int n){
	if(n <= 1) return false;
	for(int i = 2;i <= sqrt(n);i++){
		if(n % i == 0){
			return false;
		}
	}

	return true;
}


int main(){
	for(int i = 10;i <= 99;i++){
		int j = i % 10 * 10 + i / 10;
		if(sushu(i) && sushu(j)){
			cout<<i<<endl;
		}
	} 
}

import java.util.*;
public class Main{
	public static boolean isPrime(int n){
		if(n<2)
			return false;
		for(int i = 2; i<=Math.sqrt(n);i++){
			if(n%i==0)
				return false;
		}
		return true;		
	}



	public static void main(String args[]){
		int i,a,b;
		for(i=10;i<=99;i++){
			a=i%10;
			b=i/10;
			if(isPrime(i)&&isPrime(a*10+b))
				System.out.println(i);
		}
	}
}

import math
def suShu(n):
    f = True
    for i in range (2, int(math.sqrt(n))+1):
        if n % i == 0:
            f = False; 
    return f and n != 1 

def jdsuShu(n):
        a = n // 10
        b = n % 10
        return suShu(n) and suShu(b*10 +a);
for i in range (10, 100):
        if jdsuShu(i):
                print(i)

#include<stdio.h>
int m,n;
int f(int a,int b)
{
	if(a==0)	return b+1;
	if(b==0)	return f(a-1,1);
	return f(a-1,f(a,b-1));
}
int main()
{
	scanf("%d %d",&m,&n);
	printf("%d",f(m,n));
	return 0;
}

#include<bits/stdc++.h>
using namespace std;
int m,n;
int ack(int m,int n){
	if(m==0)return n+1;
	else if(m!=0&&n==0)return ack(m-1,1);
	else return ack(m-1,ack(m,n-1));
}
int main(){
	cin>>m>>n;
	cout<<ack(m,n);
    return 0;
}

def ack(m,n):
    if(m==0):
        y=n+1
    elif(n==0):
        y=ack(m-1,1)
    else:
        y=ack(m-1,ack(m,n-1))
    return(y)
sr=input().split()
m=int(sr[0])
n=int(sr[1])
print(ack(m,n))
